import React, { useState, useRef, useEffect } from "react";
import AuthService from "./../services/AuthService";
import Message from "./../component/Message";
import * as yup from "yup";








const schema = yup.object().shape({
  first_name: yup
    .string("First Name must be string")
    .required(function (value) {
      toast.warning("First name  is required");
    })
    .min(3, function (value) {
      toast.warning("First name  must be atleast 3 charachters");
    })
    .max(32, function (value) {
      toast.warning("First name  must atmost 32 chrachters");
    }),

  last_name: yup
    .string("Last Name must be string ")
    .required(function (value) {
      toast.warning("last name is required");
    })
    .min(3, function (value) {
      toast.warning("Last name  must be atleast 3 charachters");
    })
    .max(32, function (value) {
      toast.warning("Last name  must atmost 32 chrachters");
    }),

  email: yup
    .string()
    .required(function (value) {
      toast.warning("Email is required");
    })
    .email("Invalid email")
    .test("Unique Email", "Email already in use", function (value) {
      return new Promise((resolve, reject) => {
        axios
          .post("http://localhost:3001/api/users/verify/email", {
            email: value,
          })
          .then((res) => {
            if (res.data.msg === "Email already been taken") {
              toast.error(res.data.msg);
              resolve(false);
            }
            resolve(true);
          });
      });
    }),

  password: yup
    .string("Password must be string ")
    .required(function (value) {
      toast.warning("Password is required");
    })
    .min(3, function (value) {
      toast.warning("Password  must be atleast 3 charachters");
    })
    .max(32, function (value) {
      toast.warning("Password  must atmost 32 chrachters");
    }),

  c_password: yup
    .string()
    .required(function (value) {
      toast.warning("Confirm password is required");
    })
    .oneOf([yup.ref("password"), null], function (value) {
      toast.warning("Unmatched passwords");
    }),
  // agree:yup.string()
  //  .checked(function(value){
  //     toast.warning("Agree to terms and  conidtions ");
  // }),

  username: yup
    .string("Username must be string ")
    .required(function (value) {
      toast.warning("Username is required ");
    })
    .min(3, function (value) {
      toast.warning("Username  must be atleast 3 charachters");
    })
    .max(32, function (value) {
      toast.warning("Username  must atmost 32 chrachters");
    })
    .test("Unique username", "Username already in taken", function (value) {
      return new Promise((resolve, reject) => {
        axios
          .post("http://localhost:3001/api/users/verify/username", {
            username: value,
          })
          .then((res) => {
            if (res.data.msg === "Username already been taken") {
              toast.error(res.data.msg);
              resolve(false);
            }
            resolve(true);
          });
      });
    }),
});
/* Validation ends here */









const Register = (props) => {
  const [user, setUser] = useState({ username: "", password: "", role: "user" });
  const [message, setMessage] = useState(null);
  let timerID = useRef(null);
  useEffect(() => {
    return () => {
      clearTimeout(timerID);
    };
  }, []);
  const onChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    //console.log({ user });
  };

  const resetForm = () => {
    setUser({ username: "", password: "", role: "user" });
  };
  const onSubmit = (e) => {
    e.preventDefault();
    AuthService.register(user).then((data) => {
      const { message } = data;
      setMessage(message);
      resetForm();
      if (!message.msgError) {
        timerID = setTimeout(() => {
          props.history.push("/login");
        }, 2000);
      }
    });
  };
  return (
    <div className="row">
    <div className="col-md-2"></div>
    <div className="col-md-8 card text-center p-3">
    <form onSubmit={onSubmit}>
    <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="username">Username </label>
        <input
          type="text"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="username"
          value={user.username}
          required
        />
        </div>

           {/*  */}
           <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="email">Email </label>
        <input
          type="email"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="email"
          value={user.email}
          required
        />
         </div>

         {/*  */}


        <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="password">Password </label>
        <input
          type="password"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="password"
          value={user.password}
          required
        />
         </div>
         {/*  */}
         <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="c_password">Confirm Password </label>
        <input
          type="c_password"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="c_password"
          value={user.c_password}
          required
        />
         </div>

         {/*  */}
          {/*  */}
          <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="mobile">Mobile </label>
        <input
          type="mobile"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="mobile"
          value={user.mobile}
          required
        />
         </div>

         {/*  */}


         <div className="form-group form-inline">
    {/* <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="role">Role </label> */}

        <input
          type="hidden"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="role"
          value={user.role}
          required
        />
      </div>
      <div className="text-center">

        <button type="submit" className="btn btn-lg btn-success">
          Register
        </button>
        </div>
      </form>
    </div>
    <div className="col-md-2"></div>
      {message ? <Message message={message} /> : null}
    </div>
  );
};
export default Register;
