export default {
  login: (user) => {
    return fetch("/user/login", {
      method: "POST",
      body: JSON.stringify(user),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => data);
  },

  register: (user) => {
      console.log(
        " %c *** logout[http://localhost:5000/user/login] *** ",
        "font-size: 12px; font-weight: bold;color:green"
      );
    return fetch("/user/register", {
      method: "POST",
      body: JSON.stringify(user),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => data);
  },

  logout: () => {
console.log(
  " %c *** logout[http://localhost:5000/user/login] *** ",
  "font-size: 12px; font-weight: bold;color:green"
);
    return fetch("/user/logout")
      .then((res) => res.json())
      .then((data) => data);
  },
  /*
! Sync backend and frontend (even after browser closes)
! call this function with context API
*/
  isAuthenticated: () => {
    return fetch("http://localhost:5000/user/authenticated")
      .then((res) => {
        console.log(
          " %c *** isAuthenticated[http://localhost:5000/user/authenticated] *** ",
          "font-size: 12px; font-weight: bold;color:green"
        );
        if (res.status !== 401) {
          return res.json().then((data) => data);
        } else {
          return { isAuthenticated: false, user: { username: "", role: "" } };
        }
      })
      .then((data) => data);
  },
};
