import React, { useState, useContext } from "react";
import AuthService from "./../services/AuthService";
import Message from "./../component/Message";
import { AuthContext } from "./../context/AuthContext";
import loginBackgroundImage from './../assets/images/background-1.jpeg' ;
import ClockContainer from './../component/SpecialComponents/ClockContainer';
import { ToastContainer, toast } from 'react-toastify';
const Login = (props) => {
  const [user, setUser] = useState({ username: "", password: "" });
  const [message, setMessage] = useState(null);
  const authContext = useContext(AuthContext);

  const onChange = (e) => {
    e.preventDefault();

    setUser({ ...user, [e.target.name]: [e.target.value] });
    //console.log({ user });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    AuthService.login(user).then((data) => {
      const { isAuthenticated, user, message } = data;
      console.log(isAuthenticated, user, message);
      if (isAuthenticated) {
        authContext.setUser(user);
        authContext.setIsAuthenticated(isAuthenticated);
        props.history.push("/todos");
        console.log("isAuthenticated");
      } else {
        setMessage(message);

      }
    });
  };
  return (
    <div  className="row align-items-center  border border-danger"
    style={{
      backgroundImage: "url(" + loginBackgroundImage + ")",
      backgroundPosition: "center",
      backgroundSize: "cover",
      backgroundRepeat: "no-repeat",
    }}>
        <div

        >
          <ToastContainer />

          <div className="col-md-12 ">
            <div className="row ">
              <div
                className="col-md-4  "
                style={{
                  backgroundImage: "url(" + loginBackgroundImage + ")",
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                }}
              >
                {/* start side bar content  */}
                <small className="font-weight-bold text-white">
                  Not already Registered
                </small>
                <small className="font-weight-bold text-white">
                  Create a free account now{" "}
                </small>
                {/* <div className="text-white">
                <ClockContainer />
              </div> */}
                <button
                  type="button"
                  id="signupBtn"
                  className="btn btn-warning btn-block "

                >
                  <i
                    className="fa fa-user-plus fa-2x text-white"
                    aria-hidden="true"
                  ></i>
                </button>
              </div>
              {/* end side bar content  */}

              <div
                className="col-md-8 bg-dark "
                style={{
                  backgroundImage: "url(" + loginBackgroundImage + ")",
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                }}
              >
                {/* start card form  */}
                <div className="row bg-dark">
                  <div className="col-md-12 ">
                    <ClockContainer />
                  </div>
                </div>
                <h2 className="font-weight-bold text-success">Login Now </h2>
                <div className="d-flex flex-row justify-content-center align-items-center mt-3 mb-3">
                  <span className="ml-2 mr-2 ">
                    <i
                      className=" fa-2x text-primary fa fa-facebook-official"
                      aria-hidden="true"
                    ></i>
                  </span>
                  <span className="ml-2 mr-2 ">
                    <i
                      className=" fa-2x text-success fa fa-google-plus-official"
                      aria-hidden="true"
                    ></i>
                  </span>
                  <span className="ml-2 mr-2 ">
                    <i
                      className=" fa-2x text-info fa fa-twitter-square"
                      aria-hidden="true"
                    ></i>
                  </span>
                </div>
                <form onSubmit={onSubmit}>
        <input
          type="text"
          className="form-control custom-login-input"
          onChange={onChange}
          name="username"
          required
        />
        <input
          type="password"
          className="form-control custom-login-input"
          onChange={onChange}
          name="password"
          required
        />
        <button type="submit" className="btn btn-lg btn-success">
        <i className="fa fa-sign-in fa-2x" aria-hidden="true"></i>
        </button>
      </form>

                {/* end card form  */}
              </div>
            </div>
          </div>
          <div className="col-md-2"></div>
        </div>  
      {message ? <Message message={message} /> : null}
    </div>
  );
};
export default Login;
