import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AuthService from "./../services/AuthService";
import { AuthContext } from "./../context/AuthContext";

const Navbar = (props) => {
  const { isAuthenticated, user, setIsAuthenticated, setUser } = useContext(
    AuthContext
  );

  const onClickLogoutHandler = () => {
    AuthService.logout().then((data) => {
      if (data.success) {
        //resetiing the user
        //username will be empty stringand so as role
        setUser(data.user);
        setIsAuthenticated(false);
      }
    });
  };
  const authenticatedNavbar = () => {
    return (
      <>
        <Link to="/">
          <li className="nav-item nav-link">Home</li>
        </Link>
        <Link to="/todos">
          <li className="nav-item nav-link">Todos</li>
        </Link>
        {/* if user is admin show admin link  */}
        {user.role === "admin" ? (
          <Link to="/admin">
            <li className="nav-item nav-link">Admin</li>
          </Link>
        ) : null}
        <button
          className="btn btn-link nav-item nav-link "
          onClick={onClickLogoutHandler}
        ></button>
      </>
    );
  };
  const unauthenticatedNavbar = () => {
    return (
      <>
        <Link to="/">
          <li className="nav-item nav-link">Home</li>
        </Link>
        <Link to="/login">
          <li className="nav-item nav-link">Login</li>
        </Link>

        <Link to="/register">
          <li className="nav-item nav-link">Register</li>
        </Link>
      </>
    );
  };
  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarTogglerDemo01"
          aria-controls="navbarTogglerDemo01"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <div className="collapse navbar-collapse" id="navbarTogglerDemo01">
          <Link className="navbar-brand" to="/">
            React Auth
          </Link>

          <ul className="navbar-nav ml-auto mt-2 mt-lg-0">
            {!isAuthenticated ? unauthenticatedNavbar() : authenticatedNavbar()}
            <li className="nav-item  ">
              <Link to="/" className=""></Link>
            </li>
          </ul>

          <div>
            <button className="btn btn-success btn-sm  mr-2">
              <i className="fa fa-shopping-basket  "></i>0
            </button>
            <button
              onClick={() => {
                logOut();
              }}
              type="submit"
              className="btn btn-warning btn btn-sm text-danger font-weight-bold "
            >
              <i
                className="fa fa-sign-out text-danger fa-2x"
                aria-hidden="true"
              ></i>
            </button>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
