export default {
  login: (user) => {
    console.log(
      " %c *** login[http://localhost:5000/user/login][POST] *** ",
      "font-size: 12px; font-weight: bold;color:green"
    );
    console.log({ user });
    let username = user["username"][0];
    let password = user["password"][0];
    console.log(username);

    return fetch("http://localhost:5000/user/login", {
      method: "post",
      body: JSON.stringify(user),
      headers: {
        "Content-Type": "application/json",
      },
    }).then((res) => {
      if (res.status !== 401) {
        return res.json().then((data) => data);
      } else {
        return { isAuthenticated: false, user: { username: "", role: "" } };
      }
    });
  },

  register: (user) => {
    console.log(
      " %c *** register[http://localhost:5000/user/register][POST] *** ",
      "font-size: 12px; font-weight: bold;color:green"
    );
    return fetch("/user/register", {
      method: "post",
      body: JSON.stringify(user),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => data);
  },

  logout: () => {
    console.log(
      " %c *** logout[http://localhost:5000/user/login][POST] *** ",
      "font-size: 12px; font-weight: bold;color:green"
    );
    return fetch("/user/logout")
      .then((res) => res.json())
      .then((data) => data);
  },
  /*
! Sync backend and frontend (even after browser closes)
! call this function with context API
*/
  isAuthenticated: () => {
    return fetch("http://localhost:5000/user/authenticated")
      .then((res) => {
        console.log(
          " %c *** isAuthenticated[http://localhost:5000/user/authenticated][GET] *** ",
          "font-size: 12px; font-weight: bold;color:green"
        );
        if (res.status !== 401) {
          return res.json().then((data) => data);
        } else {
          return { isAuthenticated: false, user: { username: "", role: "" } };
        }
      })
      .then((data) => data);
  },
};
