  const [user, setUser] = useState({ username: "", password: "" });
  const [message, setMessage] = useState(null);
  const authContext = useContext(AuthContext);

  const onChange = (e) => {
    e.preventDefault();
    set({ ...user, [e.target.name]: [e.target.value] });
    console.log(user);
  };

  return (
    <div>
      <form>
        <input
          type="text"
          className="form-control"
          onChange={onChange}
          name="username"
          required
        />
        <input
          type="password"
