import React, { useState, useRef, useEffect } from "react";
import AuthService from "./../services/AuthService";
import Message from "./../component/Message";

const Register = (props) => {
  const [user, setUser] = useState({ username: "", password: "", role: "" });
  const [message, setMessage] = useState(null);
  let timerID = useRef(null);
  useEffect(() => {
    return () => {
      clearTimeout(timerID);
    };
  }, []);
  const onChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    //console.log({ user });
  };

  const resetForm = () => {
    setUser({ username: "", password: "", role: "admin" });
  };
  const onSubmit = (e) => {
    e.preventDefault();
    AuthService.register(user).then((data) => {
      const { message } = data;
      setMessage(message);
      resetForm();
      if (!message.msgError) {
        timerID = setTimeout(() => {
          props.history.push("/login");
        }, 2000);
      }
    });
  };
  return (
    <div className="row">
    <div className="col-md-2"></div>
    <div className="col-md-8 card text-center p-3">
    <form onSubmit={onSubmit}>
    <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="username">Username </label>
        <input
          type="text"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="username"
          value={user.username}
          required
        />
        </div>

           {/*  */}
           <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="email">Email </label>
        <input
          type="email"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="email"
          value={user.email}
          required
        />
         </div>

         {/*  */}


        <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="password">Password </label>
        <input
          type="password"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="password"
          value={user.password}
          required
        />
         </div>
         {/*  */}
         <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="c_password">Confirm Password </label>
        <input
          type="c_password"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="c_password"
          value={user.c_password}
          required
        />
         </div>

         {/*  */}
          {/*  */}
          <div className="form-group form-inline">
    <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="mobile">Mobile </label>
        <input
          type="mobile"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="mobile"
          value={user.mobile}
          required
        />
         </div>

         {/*  */}


         <div className="form-group form-inline">
    {/* <label className="btn bg-white"style={{width:"150px",textAlign:"",fontWeight:"bold"}} htmlFor="role">Role </label> */}

        <input
          type="hidden"
          className="form-control"
          onChange={onChange}
           style={{width:"50%",marginLeft:"15px",height:"35px",borderRadius:"0px!important"}}
          name="role"
          value={user.role}
          required
        />
      </div>
      <div className="text-center">

        <button type="submit" className="btn btn-lg btn-success">
          Register
        </button>
        </div>
      </form>
    </div>
    <div className="col-md-2"></div>
      {message ? <Message message={message} /> : null}
    </div>
  );
};
export default Register;
