import React, { useState, useContext } from "react";
import AuthService from "./../services/AuthService";
import Message from "./../component/Message";
import { AuthContext } from "./../context/AuthContext";
import loginBackgroundImage from './../assets/images/background-1.jpeg' ;
import ClockContainer from './../component/SpecialComponents/ClockContainer';
import ToastContainer from 'react-toastify';
const Login = (props) => {
  const [user, setUser] = useState({ username: "", password: "" });
  const [message, setMessage] = useState(null);
  const authContext = useContext(AuthContext);

  const onChange = (e) => {
    e.preventDefault();

    setUser({ ...user, [e.target.name]: [e.target.value] });
    //console.log({ user });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    AuthService.login(user).then((data) => {
      const { isAuthenticated, user, message } = data;
      console.log(isAuthenticated, user, message);
      if (isAuthenticated) {
        authContext.setUser(user);
        authContext.setIsAuthenticated(isAuthenticated);
        props.history.push("/todos");
        console.log("isAuthenticated");
      } else {
        setMessage(message);

      }
    });
  };
  return (
    <div>
      <React.Fragment>
      {props.authState ? (
        <Redirect to="/home" />
      ) : (
        <div
          className="row align-items-center login-component-wrapper"
          style={{
            backgroundImage: "url(" + loginBackgroundImage + ")",
            backgroundPosition: "center",
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
          }}
        >
          <ToastContainer />
          <div className="col-md-2"></div>
          <div className="col-md-8 ">
            <div className="row ">
              <div
                className="col-md-4 sidebar-wrapper "
                style={{
                  backgroundImage: "url(" + loginBackgroundImage + ")",
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                }}
              >
                {/* start side bar content  */}
                <small className="font-weight-bold text-white">
                  Not already Registered
                </small>
                <small className="font-weight-bold text-white">
                  Create a free account now{" "}
                </small>
                {/* <div className="text-white">
                <ClockContainer />
              </div> */}
